// src/App.js
import React, { useState } from "react";
import Canvas from "./components/Canvas";
import "./App.css";

const App = () => {
  const [color, setColor] = useState("#000000");
  const [width, setWidth] = useState(3);
  const [erasing, setErasing] = useState(false);

  const clearCanvas = () => {
    const canvas = document.querySelector("canvas");
    const context = canvas.getContext("2d");
    context.clearRect(0, 0, canvas.width, canvas.height);
  };

  const toggleEraser = () => {
    setErasing(!erasing);
    if (erasing) {
      setColor("#000000"); // Reset to default color when switching back to pencil
    }
  };

  return (
    <div className="App">
      <p id="title">Paint!</p>
      <div>
        <p>Instructions:</p>
        <ul>
          <li>Click and drag to draw on the canvas.</li>
          <li>Use the color picker to change the color.</li>
          <li>Use the line width slider to change the line width.</li>
          <li>Click the "Clear Canvas" button to clear the canvas.</li>
          <li>Click the "Switch to Eraser" button to switch to eraser mode.</li>
          <li>
            Click the "Switch to Pencil" button to switch back to pencil mode.
          </li>
        </ul>
      </div>
      <div className="canvas-container">
        <div className="controls">
          <input
            type="color"
            id="colorPicker"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            disabled={erasing}
          />

          <label htmlFor="lineWidth">Line Width:</label>
          <input
            type="range"
            id="lineWidth"
            min="1"
            max="10"
            value={width}
            onChange={(e) => setWidth(e.target.value)}
          />

          <button onClick={clearCanvas}>Clear Canvas</button>
          <button onClick={toggleEraser}>
            {erasing ? "Switch to Pencil" : "Switch to Eraser"}
          </button>
        </div>
        <Canvas color={color} width={width} erasing={erasing} />
      </div>
    </div>
  );
};

export default App;
