// src/App.js
import React, { useState } from 'react';
import Canvas from './components/Canvas';
import './App.css';

const penColors = ['#FF5733', '#33C1FF', '#8EFF33', '#FF33F0', '#3333FF'];  // Example colors

const App = () => {
    const [color, setColor] = useState('#3333FF');  // Default color
    const [width, setWidth] = useState(3);
    const [erasing, setErasing] = useState(false);

    const clearCanvas = () => {
        const canvas = document.querySelector('canvas');
        const context = canvas.getContext('2d');
        context.clearRect(0, 0, canvas.width, canvas.height);
    };

    const toggleEraser = () => {
        setErasing(!erasing);
    };

    return (
        <div className="App">
            <div className="pen-selector">
                {penColors.map((penColor, index) => (
                    <div key={index} className={`pen ${color === penColor ? 'selected' : ''}`}
                         style={{ backgroundColor: penColor }}
                         onClick={() => setColor(penColor)}>
                    </div>
                ))}
            </div>
            <div className="controls">
                <label htmlFor="lineWidth">Line Width:</label>
                <input type="range" id="lineWidth" min="1" max="10" value={width} onChange={(e) => setWidth(e.target.value)} />

                <button onClick={clearCanvas}>Clear Canvas</button>
                <button onClick={toggleEraser}>{erasing ? "Switch to Pencil" : "Switch to Eraser"}</button>
            </div>
            <Canvas color={color} width={width} erasing={erasing} />
        </div>
    );
};

export default App;
